# Beregn mean og varians
def mean_var(data):
    n = len(data)
    features = len(data[0])
    means, vars_ = [], []
    for j in range(features):
        col = [row[j] for row in data]
        mu = sum(col) / n
        var = sum((x - mu)**2 for x in col) / n
        means.append(mu)
        vars_.append(var)
    return means, vars_

meanTop, varTop = mean_var(classTop)
meanBottom, varBottom = mean_var(classBottom)
meanMix1, varMix1 = mean_var(classTop)
meanMix2, varMix2 = mean_var(classTop)

